variables.CLIENT_CONTROLLER:
function($scope, $http, $timeout) {
  var c = this;

  c.loading = true;
  c.data = [];
  c.displayData = [];
  c.columns = [];
  c.searchTerm = '';
  c.sortField = null;
  c.sortDirection = 'asc';
  c.currentPage = 1;
  c.pageSize = 20;
  c.totalPages = 1;
  c.editingCell = null;
  c.rowActions = [];

  // Parse row actions from options
  try {
    c.rowActions = JSON.parse(c.options.row_actions || '[]');
  } catch(e) {
    c.rowActions = [];
  }

  c.getData = function() {
    c.loading = true;
    c.server.get({
      action: 'get_table_data',
      table: c.options.table_name,
      max_records: c.options.max_records
    }).then(function(response) {
      c.columns = response.data.columns || [];
      c.data = response.data.data || [];
      c.filterData();
      c.loading = false;
    });
  };

  c.filterData = function() {
    var filtered = c.data;

    if (c.searchTerm) {
      filtered = c.data.filter(function(row) {
        return Object.values(row).some(function(value) {
          return String(value).toLowerCase().includes(c.searchTerm.toLowerCase());
        });
      });
    }

    // Apply sorting
    if (c.sortField) {
      filtered.sort(function(a, b) {
        var aVal = a[c.sortField] || '';
        var bVal = b[c.sortField] || '';
        var result = aVal < bVal ? -1 : aVal > bVal ? 1 : 0;
        return c.sortDirection === 'desc' ? -result : result;
      });
    }

    // Apply pagination
    c.totalPages = Math.ceil(filtered.length / c.pageSize);
    var start = (c.currentPage - 1) * c.pageSize;
    c.displayData = filtered.slice(start, start + c.pageSize);
  };

  c.sort = function(field) {
    if (c.sortField === field) {
      c.sortDirection = c.sortDirection === 'asc' ? 'desc' : 'asc';
    } else {
      c.sortField = field;
      c.sortDirection = 'asc';
    }
    c.filterData();
  };

  c.previousPage = function() {
    if (c.currentPage > 1) {
      c.currentPage--;
      c.filterData();
    }
  };

  c.nextPage = function() {
    if (c.currentPage < c.totalPages) {
      c.currentPage++;
      c.filterData();
    }
  };

  c.getCellValue = function(row, column) {
    var value = row[column.field];
    // Format based on field type
    if (column.type === 'date') {
      return value ? new Date(value).toLocaleDateString() : '';
    }
    return value || '';
  };

  c.isEditing = function(row, column) {
    return c.options.enable_inline_edit && 
           c.editingCell && 
           c.editingCell.row === row && 
           c.editingCell.column === column;
  };

  c.startEdit = function(row, column) {
    if (c.options.enable_inline_edit) {
      c.editingCell = {row: row, column: column};
    }
  };

  c.saveEdit = function(row, column) {
    c.editingCell = null;
    // Save to server
    c.server.get({
      action: 'update_record',
      table: c.options.table_name,
      sys_id: row.sys_id,
      field: column.field,
      value: row[column.field]
    });
  };

  c.handleEditKeypress = function(event, row, column) {
    if (event.keyCode === 13) { // Enter key
      c.saveEdit(row, column);
    }
  };

  c.performAction = function(action, row) {
    switch(action.action) {
      case 'view':
        window.open('/' + c.options.table_name + '.do?sys_id=' + row.sys_id, '_blank');
        break;
      case 'edit':
        window.open('/' + c.options.table_name + '.do?sys_id=' + row.sys_id + '&sysparm_view=edit', '_blank');
        break;
      default:
        // Custom action
        $scope.$emit('widget-action', {action: action, row: row});
    }
  };

  c.exportData = function() {
    var csv = c.columns.map(function(col) { return col.label; }).join(',') + '\n';
    c.displayData.forEach(function(row) {
      csv += c.columns.map(function(col) {
        var val = row[col.field] || '';
        return '"' + String(val).replace(/"/g, '""') + '"';
      }).join(',') + '\n';
    });

    var blob = new Blob([csv], {type: 'text/csv'});
    var url = window.URL.createObjectURL(blob);
    var a = document.createElement('a');
    a.href = url;
    a.download = c.data.title + '.csv';
    a.click();
  };

  // Initial load
  c.getData();
}

variables.SERVER_SCRIPT:
(function() {
  try {
    data.columns = [];
    data.data = [];
    gs.log('DataTable widget server script started', 'datatable_widget');

    if (input && input.action === 'get_table_data') {
      var tableName = input.table || 'incident';
      var maxRecords = parseInt(input.max_records) || 100;

      gs.log('Getting table data: table=' + tableName + ', maxRecords=' + maxRecords, 'datatable_widget');

      // Get table metadata
      var fields = [];
      var gd = new GlideRecord('sys_dictionary');
      gd.addQuery('name', tableName);
      gd.addQuery('internal_type', '!=', 'collection');
      gd.orderBy('column_label');
      gd.setLimit(10); // Limit columns for performance
      gd.query();

      while (gd.next()) {
        if (gd.element && !gd.element.startsWith('sys_')) {
          fields.push({
            field: gd.element.toString(),
            label: gd.column_label.toString() || gd.element.toString(),
            type: gd.internal_type.toString()
          });
        }
      }

      // Default fields if none found
      if (fields.length === 0) {
        fields = [
          {field: 'number', label: 'Number', type: 'string'},
          {field: 'short_description', label: 'Short Description', type: 'string'},
          {field: 'sys_created_on', label: 'Created', type: 'glide_date_time'}
        ];
        gs.log('Using default fields for table: ' + tableName, 'datatable_widget');
      }

      data.columns = fields;
      gs.log('Found ' + fields.length + ' columns for table', 'datatable_widget');

      // Get table data
      var gr = new GlideRecord(tableName);
      gr.orderByDesc('sys_created_on');
      gr.setLimit(maxRecords);
      gr.query();

      var recordCount = 0;
      while (gr.next()) {
        var row = {sys_id: gr.getUniqueValue()};
        fields.forEach(function(field) {
          row[field.field] = gr.getDisplayValue(field.field) || '';
        });
        data.data.push(row);
        recordCount++;
      }

      gs.log('Retrieved ' + recordCount + ' records from ' + tableName, 'datatable_widget');

    } else if (input && input.action === 'update_record') {
      // âœ… FIXED: Enhanced update with validation and error handling
      if (!input.table || !input.sys_id || !input.field) {
        throw new Error('Missing required parameters for update: table, sys_id, field');
      }

      gs.log('Updating record: table=' + input.table + ', sys_id=' + input.sys_id + ', field=' + input.field, 'datatable_widget');

      var gr = new GlideRecord(input.table);
      if (gr.get(input.sys_id)) {
        gr.setValue(input.field, input.value || '');
        gr.update();
        data.success = true;
        data.message = 'Record updated successfully';
        gs.log('Record updated successfully', 'datatable_widget');
      } else {
        data.success = false;
        data.error = 'Record not found: ' + input.sys_id;
        gs.warn('Record not found for update: ' + input.sys_id, 'datatable_widget');
      }
    }
  } catch (error) {
    gs.error('DataTable widget error: ' + error.message, 'datatable_widget');
    data.error = 'Error processing request: ' + error.message;
    data.success = false;
  }
})()
